mapper.update:
update member set
		mem_email =#{mem_email}
		,mem_pwd =#{mem_pwd}
		,mem_name =#{mem_name}
		,mem_phn =#{mem_phn}
		,mem_in_date =#{mem_in_date}
		,mem_pri_chk =#{mem_pri_chk}
		,mem_email_chk= #{mem_email_chk}
		,mem_birth= #{mem_birth}
		,mem_gender= #{mem_gender}
		,mem_loc= #{mem_loc}
		,mem_job= #{mem_job}
		,mem_reip= #{mem_reip}

	where memcode =#{mem_code}
